CREATE TRIGGER trg_CheckChildBeforeDelete\n    ON Parent\n    FOR DELETE\n    AS\n    BEGIN\n        IF EXISTS (\n            SELECT 1\n            FROM Child\n            WHERE ParentID IN (SELECT ParentID FROM DELETED)\n        )\n        BEGIN\n            RAISERROR ('Cannot delete parent record because child records exist.', 16, 1);\n            ROLLBACK TRANSACTION;\n            RETURN;\n        END\n    END;