-- Adding a computed column with a hash value using a combination of HASHBYTES\nALTER TABLE CUST_ACCT_PRFTBLT ADD HASH_KEY AS \n    CONVERT(binary(16), HASHBYTES('MD5', \n        CAST(DATA_DT_ID AS varchar) + \n        CAST(BANK_NUM AS varchar) + \n        CAST(COST_CTR_NUM AS varchar) + \n        GL_ACCT_NUM + \n        ACCT_NUM + \n        APPN_CD));\n\n-- Index the hash key\nCREATE INDEX idx_HASH_KEY ON CUST_ACCT_PRFTBLT (HASH_KEY);