SELECT a.*\nFROM CUST_ACCT_PRFTBLT a\nJOIN OTHER_TABLE b ON a.HASH_KEY = b.HASH_KEY\n                  AND a.DATA_DT_ID = b.DATA_DT_ID\n                  AND a.BANK_NUM = b.BANK_NUM\n                  AND a.COST_CTR_NUM = b.COST_CTR_NUM\n                  AND a.GL_ACCT_NUM = b.GL_ACCT_NUM\n                  AND a.ACCT_NUM = b.ACCT_NUM\n                  AND a.APPN_CD = b.APPN_CD\nWHERE b.ADDITIONAL_COLUMN = @AdditionalValue;