SELECT \n    CASE WHEN MRLSPN = 'Yes' THEN 'MRLSPN' END AS MRLSPN,\n    CASE WHEN MRLSPPN = 'Yes' THEN 'MRLSPPN' END AS MRLSPPN,\n    CASE WHEN MRLSDF = 'Yes' THEN 'MRLSDF' END AS MRLSDF,\n    CASE WHEN MRLSPDF = 'Yes' THEN 'MRLSPDF' END AS MRLSPDF,\n    CASE WHEN LGS = 'Yes' THEN 'LGS' END AS LGS,\n    CASE WHEN LGP = 'Yes' THEN 'LGP' END AS LGP,\n    CASE WHEN HOBN = 'Yes' THEN 'HOBN' END AS HOBN,\n    CASE WHEN HODF = 'Yes' THEN 'HODF' END AS HODF,\n    CASE WHEN HVO = 'Yes' THEN 'HVO' END AS HVO,\n    CASE WHEN HVOF = 'Yes' THEN 'HVOF' END AS HVOF\nFROM ProductMatrix \nWHERE ProductCode = 'MRLSPN';