-- Approach 1: Nullable Foreign Key\nALTER TABLE Product\nADD COLUMN product_manager_employee_ID INT NULL,\nADD FOREIGN KEY (product_manager_employee_ID) REFERENCES Employees(employee_ID);\n\n-- Approach 2: Association Table\nCREATE TABLE ProductManagers (\n    product_ID INT NOT NULL,\n    employee_ID INT NOT NULL,\n    UNIQUE (product_ID),\n    FOREIGN KEY (product_ID) REFERENCES Products(product_ID),\n    FOREIGN KEY (employee_ID) REFERENCES Employees(employee_ID)\n);