CREATE TABLE data_table (\n    id BIGINT AUTO_INCREMENT PRIMARY KEY,\n    entity_id INT NOT NULL,\n    date_id INT NOT NULL,\n    value_1 INT NOT NULL,\n    value_2 INT NOT NULL,\n    PRIMARY KEY (entity_id, date_id),\n    INDEX idx_entity_id (entity_id)\n) ENGINE=InnoDB\nPARTITION BY RANGE (date_id) (\n    PARTITION p0 VALUES LESS THAN (366),\n    PARTITION p1 VALUES LESS THAN (731),\n    PARTITION p2 VALUES LESS THAN (1096),\n    PARTITION p3 VALUES LESS THAN (1461),\n    PARTITION p4 VALUES LESS THAN (1826),\n    PARTITION p5 VALUES LESS THAN (2191),\n    PARTITION p6 VALUES LESS THAN (2556),\n    PARTITION p7 VALUES LESS THAN (2921),\n    PARTITION p8 VALUES LESS THAN (3286),\n    PARTITION p9 VALUES LESS THAN (3651)\n);\n\n-- Example of bulk insert for daily batch\nINSERT INTO data_table (entity_id, date_id, value_1, value_2)\nVALUES \n    (1, 1, 100, 200),\n    (2, 1, 150, 250),\n    -- Add more rows here\n    (2000000, 1, 300, 400);