-- Using char(4)\nCREATE TABLE StatusLookup (\n    StatusCode char(4) PRIMARY KEY,\n    StatusDescription varchar(50) NOT NULL\n);\n\nCREATE TABLE YourTable (\n    ID int PRIMARY KEY,\n    StatusCode char(4) NOT NULL,\n    CONSTRAINT FK_Status FOREIGN KEY (StatusCode) REFERENCES StatusLookup(StatusCode)\n);\n\n-- Sample data\nINSERT INTO StatusLookup (StatusCode, StatusDescription) VALUES ('NEW', 'New Entry');\nINSERT INTO StatusLookup (StatusCode, StatusDescription) VALUES ('PROC', 'In Process');\nINSERT INTO YourTable (ID, StatusCode) VALUES (1, 'NEW');\nINSERT INTO YourTable (ID, StatusCode) VALUES (2, 'PROC');\n\n-- Query\nSELECT yt.ID, yt.StatusCode, sl.StatusDescription\nFROM YourTable yt\nJOIN StatusLookup sl ON yt.StatusCode = sl.StatusCode;