WITH A1 AS (\n    SELECT \n        SL/SQRT(AB) AS ALPHA\n    FROM \n        tablename\n),\nA2 AS (\n    SELECT \n        ALPHA, \n        5*LOG(10, ALPHA) AS B\n    FROM \n        A1\n),\nA3 AS (\n    SELECT \n        ALPHA, \n        B, \n        2.5*LOG(10, 1 - EXP(-5/ALPHA) * ((5/ALPHA) + 1)) AS D\n    FROM \n        A2\n)\nSELECT \n    ALPHA, \n    B, \n    D, \n    BS, \n    BS - 2.74 + B + D AS BSA\nFROM \n    tablename\nJOIN \n    A3 ON tablename.AB = A3.AB; -- Assuming you have a common join key