WITH SplitTables AS (\n    SELECT TRIM(REGEXP_SUBSTR(T1.TABLENAME, '[^,]+', 1, LEVEL)) AS TABLE_NAME\n    FROM MYTABLE T1\n    CONNECT BY REGEXP_SUBSTR(T1.TABLENAME, '[^,]+', 1, LEVEL) IS NOT NULL\n    AND PRIOR T1.TABLENAME = T1.TABLENAME\n    AND PRIOR DBMS_RANDOM.VALUE IS NOT NULL\n)\nSELECT DISTINCT T1.*\nFROM MYTABLE T1\nLEFT JOIN SplitTables ST ON ST.TABLE_NAME = T1.TABLENAME\nLEFT JOIN ALL_TAB_COLS T2 ON UPPER(ST.TABLE_NAME) = UPPER(T2.TABLE_NAME) AND T2.OWNER = 'ME'\nWHERE T2.TABLE_NAME IS NULL;