BEGIN TRANSACTION;\n\nDECLARE @Counter INT = 0;\nDECLARE @BatchSize INT = 1000;\n\nWHILE @Counter < 1000000\nBEGIN\n    INSERT INTO foo (column1, column2)\n    VALUES ('1', '2'), ('1', '2'), ..., ('1', '2'); -- Repeat up to @BatchSize\n\n    SET @Counter = @Counter + @BatchSize;\n\n    IF @Counter % @BatchSize = 0\n    BEGIN\n        COMMIT TRANSACTION;\n        BEGIN TRANSACTION;\n    END\nEND\n\nCOMMIT TRANSACTION;