CREATE TRIGGER trgAfterDelete\nON YourTable\nAFTER DELETE\nAS\nBEGIN\n    DECLARE @DeletedData NVARCHAR(MAX) = ''\n    DECLARE @ColumnNames NVARCHAR(MAX) = ''\n    \n    -- Get column names\n    SELECT @ColumnNames = COALESCE(@ColumnNames + ', ', '') + COLUMN_NAME \n    FROM INFORMATION_SCHEMA.COLUMNS\n    WHERE TABLE_NAME = 'YourTable'\n    \n    -- Concatenate deleted rows into a single string\n    SELECT @DeletedData = @DeletedData + 'Row: ' + CHAR(13) + CHAR(10) + \n    (SELECT \n        STRING_AGG(COLUMN_NAME + ': ' + CAST(DELETED.COLUMN_NAME AS NVARCHAR(MAX)), ', ')\n        WITHIN GROUP (ORDER BY COLUMN_NAME)\n        FROM INFORMATION_SCHEMA.COLUMNS\n        WHERE TABLE_NAME = 'YourTable'\n    )\n    FROM DELETED\n    \n    -- Insert the concatenated text into your logging table\n    INSERT INTO YourLoggingTable (Timestamp, DeletedData)\n    VALUES (GETDATE(), @ColumnNames + CHAR(13) + CHAR(10) + @DeletedData)\nEND