DECLARE @user_id INT\n\n-- Declare a cursor for your query\nDECLARE user_cursor CURSOR FOR\nSELECT user_id FROM your_table WHERE your_conditions\n\n-- Open the cursor\nOPEN user_cursor\n\n-- Fetch the first row\nFETCH NEXT FROM user_cursor INTO @user_id\n\n-- Loop through all the rows\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    -- Execute your stored procedure for each user_id\n    EXEC your_stored_procedure @user_id\n\n    -- Fetch the next row\n    FETCH NEXT FROM user_cursor INTO @user_id\nEND\n\n-- Close and deallocate the cursor\nCLOSE user_cursor\nDEALLOCATE user_cursor