-- Assuming you have a table 'SalesData' with columns 'Date', 'ProductID', 'SalesAmount'\n\n-- Create a table to hold the synthetic data\nCREATE TABLE LargeSalesData (\n    Date DATE,\n    ProductID INT,\n    SalesAmount DECIMAL(18,2)\n);\n\n-- Insert existing data into the new table\nINSERT INTO LargeSalesData (Date, ProductID, SalesAmount)\nSELECT Date, ProductID, SalesAmount\nFROM SalesData;\n\n-- Generate synthetic data for additional years\nDECLARE @StartDate DATE = '2022-01-01'; -- Adjust based on your existing data\nDECLARE @EndDate DATE = '2031-12-31';   -- End date to generate data for 10 years\n\n-- Loop through each day and generate data\nWHILE @StartDate <= @EndDate\nBEGIN\n    INSERT INTO LargeSalesData (Date, ProductID, SalesAmount)\n    SELECT DATEADD(YEAR, YEAR(@StartDate) - YEAR(Date), Date), ProductID, SalesAmount\n    FROM SalesData\n    WHERE Date BETWEEN '2021-01-01' AND '2021-12-31'; -- Adjust based on your existing data range\n\n    SET @StartDate = DATEADD(DAY, 1, @StartDate);\nEND;\n\n-- Now you have a larger dataset in LargeSalesData