-- Inner stored procedure\nCREATE PROCEDURE InnerProc\nAS\nBEGIN\n    SELECT Column1, Column2\n    FROM YourTable\n    -- Additional logic here\nEND\nGO\n\n-- Outer stored procedure\nCREATE PROCEDURE OuterProc\nAS\nBEGIN\n    -- Declare a table variable to hold the result\n    DECLARE @ResultTable TABLE (Column1 INT, Column2 NVARCHAR(50))\n    \n    -- Insert the result of the inner stored procedure into the table variable\n    INSERT INTO @ResultTable\n    EXEC InnerProc\n    \n    -- Select from the table variable to return the result\n    SELECT Column1, Column2\n    FROM @ResultTable\nEND\nGO