-- First Indexed View\nCREATE VIEW dbo.v_Candidates\nWITH SCHEMABINDING\nAS\nSELECT\n    'Candidate' AS Source,\n    CandidateID AS SourceId,\n    LastName + ', ' + FirstName AS SourceName\nFROM dbo.Candidates;\nGO\nCREATE UNIQUE CLUSTERED INDEX IX_v_Candidates ON dbo.v_Candidates(SourceId);\nGO\n\n-- Second Indexed View\nCREATE VIEW dbo.v_Resources\nWITH SCHEMABINDING\nAS\nSELECT\n    'Resource' AS Source,\n    ResourceID AS SourceId,\n    LastName + ', ' + FirstName AS SourceName\nFROM dbo.Resources;\nGO\nCREATE UNIQUE CLUSTERED INDEX IX_v_Resources ON dbo.v_Resources(SourceId);\nGO\n\n-- Third Indexed View\nCREATE VIEW dbo.v_Deals\nWITH SCHEMABINDING\nAS\nSELECT\n    'Deal' AS Source,\n    DealID AS SourceId,\n    CONVERT(varchar, Number) + '-' + CONVERT(varchar, RevisionNumber) AS SourceName\nFROM dbo.Deals;\nGO\nCREATE UNIQUE CLUSTERED INDEX IX_v_Deals ON dbo.v_Deals(SourceId);\nGO\n\n-- Fourth Indexed View\nCREATE VIEW dbo.v_JobOrders\nWITH SCHEMABINDING\nAS\nSELECT\n    'Job Order' AS Source,\n    JobOrderID AS SourceId,\n    CustomerNumber AS SourceName\nFROM dbo.JobOrders;\nGO\nCREATE UNIQUE CLUSTERED INDEX IX_v_JobOrders ON dbo.v_JobOrders(SourceId);\nGO