DECLARE @DatabaseName NVARCHAR(50) = 'northwind';\nDECLARE @BackupDir NVARCHAR(255) = 'C:\Backup\';\nDECLARE @Date NVARCHAR(20) = CONVERT(VARCHAR(10), GETDATE(), 110); -- Format: MM-DD-YYYY\nDECLARE @FileName NVARCHAR(255);\n\n-- Construct the dynamic filename\nSET @FileName = @BackupDir + @DatabaseName + '_' + @Date + '.bak';\n\n-- Perform the backup\nBACKUP DATABASE @DatabaseName\nTO DISK = @FileName\nWITH FORMAT,\n     INIT,\n     SKIP,\n     REWIND,\n     NOUNLOAD,\n     STATS = 10;