SELECT \n    COALESCE(NULLIF(LTRIM(RTRIM(Line1)), ''), \n             NULLIF(LTRIM(RTRIM(Line2)), ''), \n             NULLIF(LTRIM(RTRIM(Line3)), ''), \n             NULLIF(LTRIM(RTRIM(City)), '') + ' ' + \n             NULLIF(LTRIM(RTRIM(State)), '') + ' ' + \n             NULLIF(LTRIM(RTRIM(PostalCode)), '')) AS MailAddress1,\n             \n    CASE \n        WHEN (Line1 IS NOT NULL AND LTRIM(RTRIM(Line1)) <> '') AND\n             (Line2 IS NOT NULL AND LTRIM(RTRIM(Line2)) <> '' OR Line3 IS NOT NULL AND LTRIM(RTRIM(Line3)) <> '' OR City IS NOT NULL AND LTRIM(RTRIM(City)) <> '')\n            THEN COALESCE(NULLIF(LTRIM(RTRIM(Line2)), ''), \n                          NULLIF(LTRIM(RTRIM(Line3)), ''), \n                          NULLIF(LTRIM(RTRIM(City)), '') + ' ' + \n                          NULLIF(LTRIM(RTRIM(State)), '') + ' ' + \n                          NULLIF(LTRIM(RTRIM(PostalCode)), ''))\n        ELSE NULL\n    END AS MailAddress2,\n\n    CASE \n        WHEN (Line1 IS NOT NULL AND LTRIM(RTRIM(Line1)) <> '' AND Line2 IS NOT NULL AND LTRIM(RTRIM(Line2)) <> '') AND\n             (Line3 IS NOT NULL AND LTRIM(RTRIM(Line3)) <> '' OR City IS NOT NULL AND LTRIM(RTRIM(City)) <> '')\n            THEN COALESCE(NULLIF(LTRIM(RTRIM(Line3)), ''), \n                          NULLIF(LTRIM(RTRIM(City)), '') + ' ' + \n                          NULLIF(LTRIM(RTRIM(State)), '') + ' ' + \n                          NULLIF(LTRIM(RTRIM(PostalCode)), ''))\n        ELSE NULL\n    END AS MailAddress3,\n\n    CASE \n        WHEN (Line1 IS NOT NULL AND LTRIM(RTRIM(Line1)) <> '' AND Line2 IS NOT NULL AND LTRIM(RTRIM(Line2)) <> '' AND Line3 IS NOT NULL AND LTRIM(RTRIM(Line3)) <> '')\n            THEN NULLIF(LTRIM(RTRIM(City)), '') + ' ' + \n                 NULLIF(LTRIM(RTRIM(State)), '') + ' ' + \n                 NULLIF(LTRIM(RTRIM(PostalCode)), '')\n        ELSE NULL\n    END AS MailAddress4\n\nFROM YourTableName