SELECT \n    i.description,\n    MONTH(j.created_at) AS registration_month,\n    COUNT(j.jobseeker_id) AS new_registrations,\n    COUNT(c.cv_id) AS uploaded_cvs\nFROM \n    tb_jobseeker AS j\nJOIN \n    tb_industry AS i ON i.industry_id = j.industry_id\nLEFT JOIN \n    tb_cv AS c ON c.jobseeker_id = j.jobseeker_id AND c.created_at BETWEEN '2009-05-01' AND '2009-05-31'\nWHERE\n    j.created_at BETWEEN '2009-05-01' AND '2009-05-31'\nGROUP BY \n    i.description, registration_month;