-- On the Teacher server\nCREATE PROCEDURE AddTeacher\n    @PersonID INT,\n    @Name NVARCHAR(100)\nAS\nBEGIN\n    INSERT INTO People (PersonID, Name, Role)\n    VALUES (@PersonID, @Name, 'Teacher');\nEND;\n\n-- On the Student server\nCREATE PROCEDURE AddStudent\n    @PersonID INT,\n    @Name NVARCHAR(100)\nAS\nBEGIN\n    INSERT INTO People (PersonID, Name, Role)\n    VALUES (@PersonID, @Name, 'Student');\nEND;