MERGE INTO USERS1 oldauth\nUSING (SELECT AUTHUSERNAME, AUTHUNIQUENAME, DESCRIPTION, MAPPINGAUTHNAME \n       FROM USERS2 \n       WHERE AUTHSOURCEID = 200) newauth\nON (oldauth.AUTHUSERNAME = newauth.AUTHUSERNAME)\nWHEN MATCHED THEN\n  UPDATE SET \n    oldauth.AUTHUNIQUENAME = newauth.AUTHUNIQUENAME,\n    oldauth.DESCRIPTION = newauth.DESCRIPTION,\n    oldauth.MAPPINGAUTHNAME = newauth.MAPPINGAUTHNAME\nWHERE oldauth.AUTHSOURCEID = 100;