DECLARE @appname VARCHAR(100);\nSET @appname = 'your_application_name'; -- Replace with your actual application name\n\nDECLARE @today DATETIME;\nSET @today = CONVERT(DATE, GETDATE());\n\nDECLARE @currentTime TIME;\nSET @currentTime = CONVERT(TIME, GETDATE());\n\nSELECT TOP 1 apps.isavailable\nFROM dbo.Applications apps\nLEFT JOIN dbo.Holidays hol\n    ON apps.applicationid = hol.applicationid\n    AND CONVERT(DATE, hol.holidaydate) = @today\nLEFT JOIN dbo.HoursOfOperations hours\n    ON apps.applicationid = hours.applicationid\nWHERE apps.applicationname = @appname\n    AND hol.holidaydate IS NULL\n    AND (\n        (DATEPART(WEEKDAY, @today) = 2 AND @currentTime BETWEEN hours.mondayopen AND hours.mondayclose) OR\n        (DATEPART(WEEKDAY, @today) = 3 AND @currentTime BETWEEN hours.tuesdayopen AND hours.tuesdayclose) OR\n        (DATEPART(WEEKDAY, @today) = 4 AND @currentTime BETWEEN hours.wednesdayopen AND hours.wednesdayclose) OR\n        (DATEPART(WEEKDAY, @today) = 5 AND @currentTime BETWEEN hours.thursdayopen AND hours.thursdayclose) OR\n        (DATEPART(WEEKDAY, @today) = 6 AND @currentTime BETWEEN hours.fridayopen AND hours.fridayclose) OR\n        (DATEPART(WEEKDAY, @today) = 7 AND @currentTime BETWEEN hours.saturdayopen AND hours.saturdayclose) OR\n        (DATEPART(WEEKDAY, @today) = 1 AND @currentTime BETWEEN hours.sundayopen AND hours.sundayclose)\n    );