-- Create the table\nCREATE TABLE TestTable (\n    id INT,\n    somedate DATE,\n    somevalue INT\n);\n\n-- Insert sample data\nINSERT INTO TestTable (id, somedate, somevalue) VALUES\n(45, '2009-01-01', 3),\n(23, '2009-01-08', 5),\n(12, '2009-02-02', 0),\n(77, '2009-02-14', 7),\n(39, '2009-02-20', 34),\n(33, '2009-03-02', 6);\n\n-- Query to calculate the running total\nSELECT id, somedate, somevalue,\n       SUM(somevalue) OVER (ORDER BY somedate) AS runningtotal\nFROM TestTable\nORDER BY somedate;