CREATE TRIGGER trg_TableName_Delete\nON TableName\nFOR DELETE\nAS\nBEGIN\n    DECLARE @PrimaryKeyValue INT;\n    DECLARE @ChangedBy NVARCHAR(128);\n    \n    -- Assuming the primary key column is named 'ID'\n    SELECT @PrimaryKeyValue = DELETED.ID FROM DELETED;\n    SELECT @ChangedBy = SYSTEM_USER; -- or use another method to get the current user\n    \n    -- Insert a log entry for the deleted row\n    INSERT INTO ChangeLog (ChangeType, TableName, PrimaryKeyValue, ColumnName, OldValue, NewValue, ChangedBy)\n    SELECT 'DELETE', 'TableName', @PrimaryKeyValue, 'ColumnName1', DELETED.ColumnName1, NULL, @ChangedBy\n    FROM DELETED;\n    \n    -- Repeat for other columns as needed\nEND;