CREATE TRIGGER trg_TableName_Update\nON TableName\nFOR UPDATE\nAS\nBEGIN\n    DECLARE @PrimaryKeyValue INT;\n    DECLARE @ChangedBy NVARCHAR(128);\n    \n    -- Assuming the primary key column is named 'ID'\n    SELECT @PrimaryKeyValue = INSERTED.ID FROM INSERTED;\n    SELECT @ChangedBy = SYSTEM_USER; -- or use another method to get the current user\n    \n    -- Log the changes for each column you are interested in\n    IF UPDATE(ColumnName1)\n    BEGIN\n        INSERT INTO ChangeLog (ChangeType, TableName, PrimaryKeyValue, ColumnName, OldValue, NewValue, ChangedBy)\n        SELECT 'UPDATE', 'TableName', @PrimaryKeyValue, 'ColumnName1', DELETED.ColumnName1, INSERTED.ColumnName1, @ChangedBy\n        FROM DELETED, INSERTED;\n    END\n\n    IF UPDATE(ColumnName2)\n    BEGIN\n        INSERT INTO ChangeLog (ChangeType, TableName, PrimaryKeyValue, ColumnName, OldValue, NewValue, ChangedBy)\n        SELECT 'UPDATE', 'TableName', @PrimaryKeyValue, 'ColumnName2', DELETED.ColumnName2, INSERTED.ColumnName2, @ChangedBy\n        FROM DELETED, INSERTED;\n    END\n    -- Repeat for other columns as needed\nEND;