DECLARE @newId UNIQUEIDENTIFIER = NEWID();\nDECLARE @parentId UNIQUEIDENTIFIER = 'your-parent-id';\nDECLARE @parentLft INT;\nDECLARE @parentRgt INT;\nDECLARE @parentDepth INT;\n\n-- Get the left and right values of the parent node\nSELECT @parentLft = lft, @parentRgt = rgt, @parentDepth = depth\nFROM categories\nWHERE id = @parentId;\n\n-- Update the existing nodes to make space for the new node\nUPDATE categories\nSET rgt = rgt + 2\nWHERE rgt >= @parentRgt;\n\nUPDATE categories\nSET lft = lft + 2\nWHERE lft > @parentRgt;\n\n-- Insert the new node\nINSERT INTO categories (id, name, lft, rgt, depth)\nVALUES (@newId, 'New Node Name', @parentRgt, @parentRgt + 1, @parentDepth + 1);