-- Step 1: Create the new Name table\nCREATE TABLE Name (\n    Id INT IDENTITY(1,1) PRIMARY KEY,\n    Name NVARCHAR(255) UNIQUE\n);\n\n-- Step 2: Insert unique names from Contacts into the Name table\nINSERT INTO Name (Name)\nSELECT DISTINCT Name\nFROM Contacts;\n\n-- Step 3: Add a foreign key column in the Contacts table\nALTER TABLE Contacts\nADD NameId INT;\n\n-- Step 4: Update the Contacts table to set the foreign key column\nUPDATE Contacts\nSET NameId = (\n    SELECT Id\n    FROM Name\n    WHERE Name.Name = Contacts.Name\n);\n\n-- Step 5: Drop the old Name column from the Contacts table\nALTER TABLE Contacts\nDROP COLUMN Name;