CREATE OR REPLACE PROCEDURE dynamic_search(\n    p_name IN VARCHAR2,\n    p_age IN NUMBER,\n    p_city IN VARCHAR2,\n    search_cursor OUT SYS_REFCURSOR\n) AS\n    v_query VARCHAR2(32767) := 'SELECT * FROM your_table WHERE 1=1';\nBEGIN\n    IF p_name IS NOT NULL THEN\n        v_query := v_query || ' AND name = :p_name';\n    END IF;\n    IF p_age IS NOT NULL THEN\n        v_query := v_query || ' AND age = :p_age';\n    END IF;\n    IF p_city IS NOT NULL THEN\n        v_query := v_query || ' AND city = :p_city';\n    END IF;\n\n    OPEN search_cursor FOR v_query\n    USING p_name, p_age, p_city;\nEND;\n/