DECLARE\n    column_is_nullable VARCHAR2(1);\nBEGIN\n    -- Check if the column is already nullable\n    SELECT NULLABLE \n    INTO column_is_nullable \n    FROM USER_TAB_COLUMNS \n    WHERE TABLE_NAME = 'MYTABLE' \n    AND COLUMN_NAME = 'MYCOLUMN';\n\n    -- If the column is not nullable, modify it to be nullable\n    IF column_is_nullable = 'N' THEN\n        EXECUTE IMMEDIATE 'ALTER TABLE MYTABLE MODIFY (MYCOLUMN NULL)';\n    END IF;\nEND;\n/