-- Step 1: Create the table to hold the unique IDs\nCREATE TABLE UniqueObjectIDs (\n    ID INT PRIMARY KEY\n);\n\n-- Step 2: Populate the table initially\nINSERT INTO UniqueObjectIDs (ID)\nSELECT ID FROM Table1\nUNION\nSELECT ID FROM Table2\nUNION\nSELECT ID FROM Table3;\n\n-- Step 3: Create triggers to keep the table updated\n-- Trigger for Table1\nCREATE TRIGGER trg_Table1_Insert_Update_Delete\nON Table1\nAFTER INSERT, UPDATE, DELETE\nAS\nBEGIN\n    -- Handle inserts and updates\n    INSERT INTO UniqueObjectIDs (ID)\n    SELECT ID FROM inserted\n    ON DUPLICATE KEY UPDATE ID = inserted.ID;\n\n    -- Handle deletes\n    DELETE FROM UniqueObjectIDs\n    WHERE ID IN (SELECT ID FROM deleted)\n    AND ID NOT IN (SELECT ID FROM Table1)\n    AND ID NOT IN (SELECT ID FROM Table2)\n    AND ID NOT IN (SELECT ID FROM Table3);\nEND;\n\n-- Repeat similar triggers for Table2 and Table3