-- Update records in Table A in TEST from DEV\nUPDATE ta\nSET ta.name = a.name\nFROM DEV.dbo.TableA a\nJOIN TEST.dbo.TableA ta ON a.name = ta.name;\n\n-- Update records in Table B in TEST from DEV\nUPDATE tb\nSET tb.item = b.item\nFROM DEV.dbo.TableB b\nJOIN DEV.dbo.TableA a ON b.fk_id = a.id\nJOIN TEST.dbo.TableA ta ON a.name = ta.name\nJOIN TEST.dbo.TableB tb ON tb.item = b.item AND tb.fk_id = ta.id;