CREATE PROCEDURE spMyStoredProcedure\n    @List VARCHAR(255)\nAS\nBEGIN\n    -- Remove any trailing commas and split the list into a table of values\n    SET @List = RTRIM(LTRIM(@List));\n    \n    -- Initialize the SQL query\n    DECLARE @SQL NVARCHAR(MAX) = 'SELECT ID FROM YourTable WHERE 1 = 0';\n\n    -- Dynamically build the SQL query based on the list\n    IF CHARINDEX('1', @List) > 0\n        SET @SQL = @SQL + ' OR col1 = 1';\n    IF CHARINDEX('2', @List) > 0\n        SET @SQL = @SQL + ' OR col2 = 1';\n    IF CHARINDEX('3', @List) > 0\n        SET @SQL = @SQL + ' OR col3 = 1';\n    IF CHARINDEX('4', @List) > 0\n        SET @SQL = @SQL + ' OR col4 = 1';\n\n    -- Execute the dynamic SQL\n    EXEC sp_executesql @SQL;\nEND