CREATE TRIGGER trg_UpdateGlobalMiles\n    ON MilesLog\n    AFTER INSERT\n    AS\n    BEGIN\n        DECLARE @TotalMiles INT;\n        \n        SELECT @TotalMiles = SUM(Miles) FROM MilesLog;\n\n        -- Option 1: Update a GlobalMiles table\n        UPDATE GlobalMiles SET TotalMiles = @TotalMiles;\n\n        -- Option 2: Use a cache mechanism or a service to communicate with SignalR\n        -- Example: Service call to update the cache and broadcast via SignalR\n    END;