-- Step 1: Add the new column with a default value\nALTER TABLE tbl ADD col INTEGER NOT NULL DEFAULT 1;\n\n-- Step 2: Ensure the default value is applied to all existing rows\n/* This step is not strictly necessary as the default value will be applied to all existing rows automatically */\n\n-- Step 3: Drop the default constraint\nDECLARE @ConstraintName NVARCHAR(200);\n\n-- Retrieve the name of the default constraint\nSELECT @ConstraintName = df.name\nFROM sys.columns c\nJOIN sys.default_constraints df\n    ON c.default_object_id = df.object_id\nWHERE c.object_id = OBJECT_ID('tbl') AND c.name = 'col';\n\n-- Drop the default constraint\nEXEC('ALTER TABLE tbl DROP CONSTRAINT ' + @ConstraintName);