CREATE OR REPLACE FUNCTION sp_list_name(v_start_name VARCHAR)\nRETURNS SETOF v_player AS $$\nDECLARE\n    r_player v_player%ROWTYPE;\n    v_temp VARCHAR;\nBEGIN\n    -- Construct the search pattern safely\n    v_temp := v_start_name || '%';\n\n    -- Use a FOR loop to iterate over the results\n    FOR r_player IN\n        SELECT first_name, last_name FROM v_player WHERE last_name LIKE v_temp\n    LOOP\n        RETURN NEXT r_player;\n    END LOOP;\n\n    RETURN;\nEND;\n$$ LANGUAGE plpgsql VOLATILE;