DECLARE @OriginalTable NVARCHAR(128) = 'OriginalTable';\nDECLARE @BackupTable NVARCHAR(128) = 'BackupTable';\n\nSELECT \n    'CREATE ' + \n    CASE WHEN i.is_unique = 1 THEN 'UNIQUE ' ELSE '' END +\n    i.type_desc COLLATE DATABASE_DEFAULT + ' INDEX ' + \n    QUOTENAME(i.name) + ' ON ' + \n    QUOTENAME(@BackupTable) + ' (' +\n    STRING_AGG(QUOTENAME(c.name) + \n    CASE WHEN ic.is_descending_key = 1 THEN ' DESC' ELSE ' ASC' END, ', ') WITHIN GROUP (ORDER BY ic.index_column_id) +\n    ')' + \n    CASE WHEN i.has_filter = 1 THEN ' WHERE ' + i.filter_definition ELSE '' END + ';'\nFROM \n    sys.indexes i\n    INNER JOIN sys.index_columns ic ON i.object_id = ic.object_id AND i.index_id = ic.index_id\n    INNER JOIN sys.columns c ON ic.object_id = c.object_id AND ic.column_id = c.column_id\nWHERE \n    i.object_id = OBJECT_ID(@OriginalTable)\n    AND i.is_primary_key = 0  -- Exclude primary keys if you don't want to copy them\nORDER BY \n    i.index_id;