DECLARE @OriginalTable NVARCHAR(128) = 'OriginalTable';\nDECLARE @BackupTable NVARCHAR(128) = 'BackupTable';\n\nSELECT \n    'ALTER TABLE ' + QUOTENAME(@BackupTable) + \n    ' ADD CONSTRAINT ' + QUOTENAME(fk.name) + \n    ' FOREIGN KEY (' + STRING_AGG(QUOTENAME(col.name), ', ') WITHIN GROUP (ORDER BY col.column_id) + ')' +\n    ' REFERENCES ' + QUOTENAME(OBJECT_NAME(fk.referenced_object_id)) + \n    ' (' + STRING_AGG(QUOTENAME(refcol.name), ', ') WITHIN GROUP (ORDER BY refcol.column_id) + ');'\nFROM \n    sys.foreign_keys fk\n    INNER JOIN sys.foreign_key_columns fkc ON fk.object_id = fkc.constraint_object_id\n    INNER JOIN sys.columns col ON fkc.parent_object_id = col.object_id AND fkc.parent_column_id = col.column_id\n    INNER JOIN sys.columns refcol ON fkc.referenced_object_id = refcol.object_id AND fkc.referenced_column_id = refcol.column_id\nWHERE \n    fk.parent_object_id = OBJECT_ID(@OriginalTable)\nORDER BY \n    fk.name;