-- Enable xp_cmdshell if it is not already enabled\nEXEC sp_configure 'show advanced options', 1;\nRECONFIGURE;\nEXEC sp_configure 'xp_cmdshell', 1;\nRECONFIGURE;\n\n-- Read the value of the PATH environment variable\nDECLARE @envVariable NVARCHAR(255);\nEXEC xp_cmdshell 'echo %PATH%', no_output;\nSET @envVariable = (SELECT output FROM OPENROWSET(BULK 'C:\temp\env_var.txt', SINGLE_CLOB) AS ENV_VAR);\n\n-- Print the environment variable for verification\nPRINT @envVariable;