DECLARE\n    TYPE MainTableType IS TABLE OF MainTable%ROWTYPE;\n    MainTableData MainTableType;\n    l_sql VARCHAR2(4000);\n    l_value VARCHAR2(4000);\nBEGIN\n    -- Select all data from MainTable into a collection\n    SELECT * BULK COLLECT INTO MainTableData FROM MainTable;\n    \n    -- Loop through each record in MainTable\n    FOR i IN 1..MainTableData.COUNT LOOP\n        -- Construct dynamic SQL to fetch the value from the dynamically specified table and column\n        l_sql := 'SELECT ' || MainTableData(i).COLUMNNAME || \n                 ' FROM ' || MainTableData(i).TABLENAME || \n                 ' WHERE ' || MainTableData(i).TABLENAME || '.numerator = :numerator';\n        \n        -- Execute the dynamic SQL\n        EXECUTE IMMEDIATE l_sql INTO l_value USING MainTableData(i).NUMERATOR;\n        \n        -- Output the result\n        DBMS_OUTPUT.PUT_LINE('ID: ' || MainTableData(i).ID || \n                             ', NUMERATOR: ' || MainTableData(i).NUMERATOR || \n                             ', VALUE: ' || l_value || \n                             ', TABLENAME: ' || MainTableData(i).TABLENAME || \n                             ', COLUMNNAME: ' || MainTableData(i).COLUMNNAME);\n    END LOOP;\nEND;\n/