SELECT \n    t.SalesManCode,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 1  THEN t.TargetValue ELSE 0 END) AS JanTar,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 1  THEN s.AchievedValue ELSE 0 END) AS JanAch,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 2  THEN t.TargetValue ELSE 0 END) AS FebTar,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 2  THEN s.AchievedValue ELSE 0 END) AS FebAch,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 3  THEN t.TargetValue ELSE 0 END) AS MarTar,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 3  THEN s.AchievedValue ELSE 0 END) AS MarAch,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 4  THEN t.TargetValue ELSE 0 END) AS AprTar,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 4  THEN s.AchievedValue ELSE 0 END) AS AprAch,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 5  THEN t.TargetValue ELSE 0 END) AS MayTar,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 5  THEN s.AchievedValue ELSE 0 END) AS MayAch,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 6  THEN t.TargetValue ELSE 0 END) AS JunTar,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 6  THEN s.AchievedValue ELSE 0 END) AS JunAch,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 7  THEN t.TargetValue ELSE 0 END) AS JulTar,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 7  THEN s.AchievedValue ELSE 0 END) AS JulAch,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 8  THEN t.TargetValue ELSE 0 END) AS AugTar,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 8  THEN s.AchievedValue ELSE 0 END) AS AugAch,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 9  THEN t.TargetValue ELSE 0 END) AS SepTar,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 9  THEN s.AchievedValue ELSE 0 END) AS SepAch,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 10 THEN t.TargetValue ELSE 0 END) AS OctTar,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 10 THEN s.AchievedValue ELSE 0 END) AS OctAch,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 11 THEN t.TargetValue ELSE 0 END) AS NovTar,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 11 THEN s.AchievedValue ELSE 0 END) AS NovAch,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 12 THEN t.TargetValue ELSE 0 END) AS DecTar,\n    SUM(CASE WHEN MONTH(s.SaleDate) = 12 THEN s.AchievedValue ELSE 0 END) AS DecAch\nFROM \n    MonthlyTarget t\nLEFT JOIN \n    MonthlySales s ON t.SalesManCode = s.SalesManCode\nGROUP BY \n    t.SalesManCode;