SELECT\n    mt.SalesManCode,\n    MAX(CASE WHEN MONTH(mt.TargetMonthYear) = 1 THEN mt.TargetValue END) AS JanTar,\n    MAX(CASE WHEN MONTH(ms.SaleDate) = 1 THEN ms.AchievedValue END) AS JanAch,\n    MAX(CASE WHEN MONTH(mt.TargetMonthYear) = 2 THEN mt.TargetValue END) AS FebTar,\n    MAX(CASE WHEN MONTH(ms.SaleDate) = 2 THEN ms.AchievedValue END) AS FebAch,\n    -- Repeat for other months\n    MAX(CASE WHEN MONTH(mt.TargetMonthYear) = 12 THEN mt.TargetValue END) AS DecTar,\n    MAX(CASE WHEN MONTH(ms.SaleDate) = 12 THEN ms.AchievedValue END) AS DecAch\nFROM\n    MonthlyTarget mt\nLEFT JOIN \n    MonthlySales ms ON mt.SalesManCode = ms.SalesManCode\nGROUP BY\n    mt.SalesManCode\nORDER BY\n    mt.SalesManCode;