BEGIN TRANSACTION;\n\n-- Disable constraints\nALTER TABLE YourTable NOCHECK CONSTRAINT ALL;\n\n-- Perform your operations\nINSERT INTO YourTable (Column1, Column2) VALUES ('Value1', 'Value2');\nUPDATE YourTable SET Column1 = 'NewValue' WHERE Column2 = 'Value2';\n\n-- Enable constraints\nALTER TABLE YourTable CHECK CONSTRAINT ALL;\n\n-- Check constraints manually (optional but recommended)\n-- This step depends on your specific constraints and data structure\n-- Example: SELECT * FROM YourTable WHERE NOT EXISTS (SELECT 1 FROM OtherTable WHERE OtherTable.ID = YourTable.ForeignKeyColumn);\n\n-- If all checks pass, commit the transaction\nCOMMIT TRANSACTION;