SELECT \n    c.name AS ColumnName,\n    t.name AS TypeName,\n    t.is_nullable,\n    t.max_length,\n    t.is_fixed_length\nFROM \n    sys.columns AS c\nINNER JOIN \n    sys.types AS t ON c.user_type_id = t.user_type_id\nWHERE \n    c.object_id = OBJECT_ID('YourTableName')\nORDER BY \n    c.column_id;