SELECT\n       'PROCEDURE' AS ObjectType,\n       name AS ObjectName\n   FROM sys.procedures\n   UNION ALL\n   SELECT\n       'TRIGGER' AS ObjectType,\n       name AS ObjectName\n   FROM sys.triggers\n   UNION ALL\n   SELECT\n       'VIEW' AS ObjectType,\n       name AS ObjectName\n   FROM sys.views\n   UNION ALL\n   SELECT\n       'FUNCTION' AS ObjectType,\n       name AS ObjectName\n   FROM sys.objects\n   WHERE type IN ('FN', 'IF', 'TF', 'FS', 'FT');