WITH RankedPeople AS (\n    SELECT \n        p.Id,\n        p.Name,\n        p.Bio,\n        p.Location,\n        g.RecordDate,\n        ROW_NUMBER() OVER (PARTITION BY p.Name ORDER BY g.RecordDate DESC) AS rn\n    FROM \n        People p\n    JOIN \n        GroupRecords g ON p.GroupRecordsId = g.Id\n    WHERE \n        YEAR(g.SchoolYear) = 2000\n)\nSELECT \n    Id,\n    Name,\n    Bio,\n    Location,\n    RecordDate\nFROM \n    RankedPeople\nWHERE \n    rn = 1;