SELECT\n    OrganizationName,\n    ISNULL([Current], 0) AS Current,\n    ISNULL([30-60 days], 0) AS [30-60 days],\n    ISNULL([60-90 days], 0) AS [60-90 days],\n    ISNULL([over 90 days], 0) AS [over 90 days],\n    (ISNULL([Current], 0) + ISNULL([30-60 days], 0) + ISNULL([60-90 days], 0) + ISNULL([over 90 days], 0)) AS Total\nFROM\n(\n    SELECT\n        OrganizationName,\n        ReceivableStatus,\n        InvoiceFee\n    FROM v_InvoicesFreelanceOutstanding\n) AS SourceTable\nPIVOT\n(\n    SUM(InvoiceFee)\n    FOR ReceivableStatus IN ([Current], [30-60 days], [60-90 days], [over 90 days])\n) AS PivotTable\nORDER BY OrganizationName;