SELECT \n    request_session_id AS SessionID,\n    resource_type AS ResourceType,\n    resource_database_id AS DatabaseID,\n    resource_associated_entity_id AS ResourceEntityID,\n    request_mode AS LockMode,\n    request_status AS LockStatus\nFROM sys.dm_tran_locks\nWHERE resource_database_id = DB_ID('YourDatabaseName')\n  AND resource_associated_entity_id = OBJECT_ID('YourTableName');