DECLARE @idoc INT;\nDECLARE @xml XML;\nSET @xml = '<Field FieldRowId="1000">\n              <Items>\n                  <Item Name="CODE"/>\n                  <Item Name="DATE"/>\n              </Items>\n            </Field>';\n\n-- Prepare the document\nEXEC sp_xml_preparedocument @idoc OUTPUT, @xml;\n\n-- Insert into temporary table\nINSERT INTO @tmpField ([name], [fieldRowId])\nSELECT \n    Item.Name AS [name],\n    Field.FieldRowId AS [fieldRowId]\nFROM \n    OPENXML(@idoc, '/Field/Items/Item', 1) WITH (\n        Name NVARCHAR(100) '../@Name',\n        FieldRowId INT '../../@FieldRowId'\n    ) AS Item;\n\n-- Clean up\nEXEC sp_xml_removedocument @idoc;