SELECT \n    har_autos.*,\n    har_automodels.Model,\n    har_automodels.MakeID,\n    har_automakes.Make,\n    har_autotypes.AutoType,\n    (\n        SELECT \n            CONCAT('[', GROUP_CONCAT(\n                JSON_OBJECT(\n                    'ID', har_notes.ID, \n                    'NoteTypeID', har_notes.NoteTypeID, \n                    'Text001', har_notes.Text001, \n                    'Value', har_notelinks.Value\n                )\n            ), ']')\n        FROM \n            har_notes\n        INNER JOIN \n            har_notelinks ON har_notes.ID = har_notelinks.NoteID\n        WHERE \n            har_autos.ID = har_notelinks.AutoID \n            AND har_notes.NoteTypeID = 1\n    ) AS UserNotes,\n    (\n        SELECT \n            CONCAT('[', GROUP_CONCAT(\n                JSON_OBJECT(\n                    'ID', har_notes.ID, \n                    'NoteTypeID', har_notes.NoteTypeID, \n                    'Text001', har_notes.Text001, \n                    'Value', har_notelinks.Value\n                )\n            ), ']')\n        FROM \n            har_notes\n        INNER JOIN \n            har_notelinks ON har_notes.ID = har_notelinks.NoteID\n        WHERE \n            har_autos.ID = har_notelinks.AutoID \n            AND har_notes.NoteTypeID = 2\n    ) AS EngineeringNotes\nFROM \n    har_autos\nLEFT JOIN \n    har_automodels ON har_autos.ModelID = har_automodels.ID\nLEFT JOIN \n    har_automakes ON har_automodels.MakeID = har_automakes.ID\nLEFT JOIN \n    har_autotypes ON har_autos.AutoTypeID = har_autotypes.ID\nWHERE \n    har_autos.ID = 1;