CREATE FUNCTION dbo.RoundToNearestFiveCents (@inputValue DECIMAL(10, 2))\nRETURNS DECIMAL(10, 2)\nAS\nBEGIN\n    DECLARE @roundedValue DECIMAL(10, 2);\n   \n    -- Calculate the remainder when divided by 0.05\n    DECLARE @remainder DECIMAL(10, 2) = @inputValue % 0.05;\n    \n    -- If the remainder is less than 0.025, round down, otherwise round up\n    IF @remainder < 0.025\n        SET @roundedValue = @inputValue - @remainder;\n    ELSE\n        SET @roundedValue = @inputValue + (0.05 - @remainder);\n    \n    RETURN @roundedValue;\nEND;\nGO