-- Create an object type\nCREATE TYPE t_person AS OBJECT (\n  id INTEGER,\n  first_name VARCHAR2(30),\n  last_name VARCHAR2(30)\n);\n\n-- Create a nested table type of the object\nCREATE TYPE t_person_table AS TABLE OF t_person;\n\n-- Create a table that uses the nested table type\nCREATE TABLE department (\n  dept_id INTEGER,\n  dept_name VARCHAR2(50),\n  employees t_person_table\n) NESTED TABLE employees STORE AS employees_tab;\n\n-- Insert data into the table\nINSERT INTO department VALUES (\n  1,\n  'HR',\n  t_person_table(\n    t_person(1, 'John', 'Doe'),\n    t_person(2, 'Jane', 'Smith')\n  )\n);\n\n-- Query data from the table\nSELECT d.dept_name, p.first_name, p.last_name\nFROM department d,\n     TABLE(d.employees) p;